import axios from "axios";

export const Notice = () => {

  const [notice,setNotice] = useState([]);

  const getData = async () => {
      try {
          const response = await axios.get("http://127.0.0.1:8001/v1/api/notice")
          //console.log(response.data);
          setNotice(response.data.Contact);
      } catch (error) {
          console.log(error.message);
      }
  };
  
  useEffect(() => {
      getData()
