import React, { useState } from 'react';
import HomePage from './HomePage';
import AboutPage from './AboutPage';
import ContactPage from './ContactPage';
import Navbar from './Navbar';

function PayCirtificate() {
  const [showNavbar, setShowNavbar] = useState(true);

  const hideNavbarOnPages = ['/about', '/contact']; // Add paths of pages where you want to hide the navbar

  const handlePageChange = (newPage) => {
    if (hideNavbarOnPages.includes(newPage)) {
      setShowNavbar(false);
    } else {
      setShowNavbar(true);
    }
  };

  return (
    <div>
      {showNavbar && <Navbar />}
      <button onClick={() => handlePageChange('/home')}>Go to Home</button>
      <button onClick={() => handlePageChange('/about')}>Go to About</button>
      <button onClick={() => handlePageChange('/contact')}>Go to Contact</button>

      {/* Render pages */}
      <HomePage />
      <AboutPage />
      <ContactPage />
    </div>
  );
}

export default App;
