import React, { useEffect, useState } from "react";
import axios from "axios";

const dist=['Alipurduar','Bankura','Paschim Bardhaman','Purba Bardhaman','Birbhum','Cooch Behar','Darjeeling','Dakshin Dinajpur','Hooghly','Howrah','Jalpaiguri','Jhargram','Kolkata','Kalimpong','Malda','Paschim Medinipur','Purba Medinipur','Murshidabad','Nadia','North 24 Parganas','South 24 Parganas','Purulia','Uttar Dinajpur']


export const Search = () => {
  const [search,setSearch]=useState({
    blood:"",
    location:""
  })
  const [res ,setRes] = useState()

  const getDonor = ()=>{
    axios.get("http://127.0.0.1:8001/v1/api/search")
    .then((res)=>setRes(res.data))
    .catch((err)=>console.log(err.message))
  }
useEffect(()=>{
  getDonor()
},[])


const handleInput=(e)=>{
  const {name,value} = e.target
  setSearch({...search,[name]:value})
}

console.log(res)

const handleSearch=()=>{
  
}





  return (
    <>
    <form onChange={handleSearch} id="form-select" >
    <h2>FIND BLOOD AVAILABILITY</h2><br />
        <label >| Blood Group |</label>
      <select
        class="form-select form-select-sm"
        aria-label=".form-select-sm example"
        onChange={handleInput}
        name="blood"
        value={search.blood}
      >
        <option   disabled selected>-----Select-----</option>
        <option value="O+">O+</option>
        <option value="O-">O-</option>
        <option value="A+">A+</option>
        <option value="A-">A-</option>
        <option value="B+">B+</option>
        <option value="B-">B-</option>
        <option value="AB+">AB+</option>
        <option value="AB-">AB-</option>
        <option value="NOT KNOWN">NOT KNOWN</option>
      </select>

      <label >| Select District |</label>
      <select
        class="form-select form-select-sm"
        aria-label=".form-select-sm example"
        onChange={handleInput}
        name="location"
        value={search.location}
      >
        <option disabled selected value="">-----Select-----</option>
       {dist.map((el)=>(
        <option value={el}>{el}</option>
       ))}
      </select>
    <br />
      <input type="submit"  value="Search" className="btn btn-danger"/>

      </form>
     {
      res.map((el)=>(el.bloodgroup))
     }




    </>
  );
};
