const logFunc =async(data)=>{
  try {
   let res =  await fetch("http://127.0.0.1:8001/v1/api/login",{
    method:"POST",
    headers:{
"Application-Type":"text/json"
    },
    body:JSON.stringify(data)
   })
   let output = await res.json()
    console.log(output)
    //alert(output.message)
    localStorage.setItem('Token',JSON.stringify(output.User))
  } catch (error) {
    alert(error.message) 
  }
 
}




export const Login = () => {
  const[luser,setLuser]=useState({
    email:"",password:""
  })
  const [show,setShow] = useState(false)
   
const handleInput=(e)=>{
  const {name , value}=e.target
  setLuser({...luser,[name]:value})
}
 const handleSubmitLog=(e)=>{
   e.preventDefault()
   logFunc(luser)
 }



    return(
        <>
    <form className="row g-3" onSubmit={handleSubmitLog}>
  <div className="col-12">
    <label htmlFor="inputEmail4" className="form-label">Email</label>
    <input onChange={handleInput} placeholder="abc@gmail.com" type="email" className="form-control" id="inputEmail4" value={luser.email} name="email"/>
  </div>
  <div className="col-12">
    <label htmlFor="inputPassword4" className="form-label">Password</label>
   <input  onChange={handleInput} minLength={10} placeholder="********" type={show ? "text":"password"} className="form-control" id="inputPassword4" value={luser.password} name="password"/>
    <input type="checkbox" onChange={()=>setShow(prev=>!prev)}/>
  </div>
  <div className="col-12">
  </div>
  <div className="col-12">
    <input type="submit" value="Log In !!" className="btn btn-danger" />
  </div>
  <h6>Do you want to Sign Up ?<a href="/signup"><span>Sign Up</span></a></h6>
</form>
        </>
    );
};
