import React, { useState } from "react";
import axios from "axios";

// const uploadData = async(data) => {
//   try {
    
//   } catch (error) {
    
//   }
// }

export const Signup = () => {
  const [user,setUser]= useState({
    name:"",
    email:"",
    password:"",
    timestamp: new Date().toLocaleDateString()
  })
//console.log(user)
const handleInput = (e) => {
  const {name,value} = e.target 
  setUser({...user,[name]:value})
}

const handleSign = (e) => {
  e.preventDefault()
  setUser({
    name:"",
    email:"",
    pass:""
  })
  setTimeout(()=>{
    alert("User added successfully !")
  },)
}



    return(
        <>
        <form className="row g-3">
        <div className="col-12">
    <label for="inputName4" className="form-label">Name</label>
    <input placeholder="Name" type="text" className="form-control" id="inputName4" onChange={handleInput} name='name' value={user.name}/>
  </div>
  <div className="col-12">
    <label for="inputEmail4" className="form-label">Email</label>
    <input placeholder="abc@gmail.com" type="email" className="form-control" onChange={handleInput} name='email' value={user.email} id="inputEmail4"/>
  </div>
  <div className="col-12">
    <label for="inputPassword4" className="form-label">Password</label>
    <input placeholder="********" type="password" className="form-control" onChange={handleInput}  name='password' value={user.password} id="inputPassword4"/>
  </div>
  <div className="col-12">
    <div className="form-check">
      <input className="form-check-input" type="checkbox" id="gridCheck"/>
      <label className="form-check-label" for="gridCheck">
        Remember Me
      </label>
    </div>
  </div>
  <div className="col-12">
    <button onClick={handleSign} type="submit" className="btn btn-danger">Sign Up</button>
  </div>
  <h6>Already Registered ? <a href="/login"><span>Log In</span></a></h6>
</form>
        </>
    );
};
