import React, { useEffect, useState } from "react";
import axios from "axios";

export const Notice = () => {

  const [notice,setNotice] = useState([]);

  const getNotice = async () => {
      try {
          const response = await axios.get("http://127.0.0.1:8001/v1/api/notice")
          //console.log(response.data);
          setNotice(response.data.Notice);
      } catch (error) {
          console.log(error.message);
      }
  };
  
  useEffect(() => {
    getNotice()
  },[]);


  return (
    <>
    <div>
          <table className="usertable">
            <thead>
              <tr>
                <th scope="col">Id</th>
                <th scope="col">Date</th>
                <th scope="col">Title</th>
                <th scope="col">Content</th>
              </tr>
            </thead>
            {notice && Array.isArray(notice) && notice.map((el)=>(
            <tbody>
              <tr>
                <th scope="row">{el._id}</th>
                <td>{el.date}</td>
                <td>{el.title}</td>
                <td>{el.content}</td>
              </tr>
            </tbody>
            ))}
          </table>
          </div>
         
    <div style="text-align: center;">
        <h1>eBloodBank</h1>
        <hr/>
        <h2>Notice</h2>
        <p><strong>Date:{el.date}</strong></p>
    </div>
    <div style="margin: 20px;">
        <h3>Topic/Heading/Subject: {el.title}</h3>
        <p>{el.content}</p>
    </div>
    <div style="text-align: right; margin-top: 30px;">
        <p>Signature: [Insert Signature]</p>
        <p>Name: [Insert Name]</p>
        <p>Designation: [Insert Designation]</p>
    </div>
    </>
  )
}
