import React, { useState } from "react";
import axios from "axios";

export function Payment() {
  const [screenshot, setScreenshot] = useState(null);
  const authtoken = localStorage.getItem("Token");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setScreenshot(file);
  };

  const handleImageUpload = () => {
    if (!screenshot) {
      alert("Please choose an image to upload.");
      return;
    }

    // Create a FormData object to send the file data
    const formData = new FormData();
    formData.append("image", screenshot);

    axios
      .post("http://127.0.0.1:8001/v1/api/upload", formData, {
        headers: {
          Authorization: authtoken,
          "Content-Type": "multipart/form-data", // Set content type for file upload
        },
      })
      .then((res) => {
        alert(res.data.message);
      })
      .catch((err) => {
        console.error(err);
        alert("An error occurred while uploading the image.");
      });
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "80vh",
      }}
    >
      <div className="payment_image">
        <h2 style={{ padding: "2px", textAlign: "center" }}>Pay Here</h2>
        <div className="qr_code">
          <img
            src="https://static.toiimg.com/thumb/resizemode-4,msid-89052864,imgsize-7302,width-364/89052864.jpg"
            alt="qr code"
          />
        </div>
        <div>
          <h2 style={{ padding: "2px", textAlign: "center" }}>
            Upload your screenshot
          </h2>
          <input
            type="file"
            name="image"
            className="form-control choose_image"
            onChange={handleImageChange}
          />
          <br />
          <button
            style={{
              width: "50%",
              margin: "auto",
              display: "block",
              backgroundColor: "red",
              border: "0",
            }}
            onClick={handleImageUpload}
            className="btn btn-info upload_image"
          >
            Upload Image
          </button>
        </div>
      </div>
    </div>
  );
}
