    //       alt=""
    //     />
    //   </div>
    //     <h2>Upload your screenshot</h2>
    //   <div className="payhead2">
    //     <input
    //       type="file"
    //       name="image"
    //       className="form-control"
    //       onChange={handleImageChange}
    //     />
    //     <button onClick={handleImageUpload} className="btn btn-danger">
    //       Upload Image
    //     </button>
    //   </div>
    // </div>
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
    <div className="payment_image">
      <div className="qr_code">
        <img
          src="https://static.toiimg.com/thumb/resizemode-4,msid-89052864,imgsize-7302,width-364/89052864.jpg"
          alt="qr code"
        />
      </div>
      <div>
        <h2>Image Upload</h2>
        <input
          type="file"
          name="image"
          className="form-control choose_image"
          onChange={handleImageChange}
        />
        <br />
        <button  style={{
    width: "50%",
    margin: "auto",
    display: "block" ,// Ensure the button takes up the full width
    backgroundColor: '#cf3683',
    border:"0"
  }}
          onClick={handleImageUpload}
          className="btn btn-info upload_image"
        >
          Upload Image
        </button>
      </div>
    </div>
  </div>
);
}
