import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router";

const dist = [
  "Alipurduar",
  "Bankura",
  "Paschim Bardhaman",
  "Purba Bardhaman",
  "Birbhum",
  "Cooch Behar",
  "Darjeeling",
  "Dakshin Dinajpur",
  "Hooghly",
  "Howrah",
  "Jalpaiguri",
  "Jhargram",
  "Kolkata",
  "Kalimpong",
  "Malda",
  "Paschim Medinipur",
  "Purba Medinipur",
  "Murshidabad",
  "Nadia",
  "North 24 Parganas",
  "South 24 Parganas",
  "Purulia",
  "Uttar Dinajpur",
];

const bgroup = ["O+", "O-", "A+", "A-", "B+", "B-", "AB+", "AB-"];

const donorData = (donor) => {
  axios
    .post("http://127.0.0.1:8001/v1/api/donate", donor)
    .then((res) => alert(res.data))
    .catch((err) => console.log(err.message));
};
export const Donate = () => {

  const navigate = useNavigate();
  const [donor, setDonor] = useState({
    name: "",
    gender: "",
    bloodgroup: "",
    bloodunit:"",
    birthyear: "",
    mobile: "",
    email: "",
    address: "",
  });
  const handleInput = (e) => {
    const { name, value } = e.target;
    setDonor({ ...donor, [name]: value });
  };
  const handleSign = (e) => {
    e.preventDefault();
    donorData(donor);
    setDonor({
      name: "",
      gender: "",
      bloodgroup: "",
      bloodunit:"",
      birthyear: "",
      mobile: "",
      email: "",
      address: "",
    });
    setTimeout(()=>{
      navigate("/paycirtificate");
    },1000);
  };

  let year = [];
  for (let i = 1990; i <= 2005; i++) {
    year.push(i);
  }
  // console.log(year);
  
  let unit = [];
  for (let i = 0; i < 100 ; i++){
    unit.push(i);
  }

  return (
    <>
      <form onSubmit={handleSign} className="row g-3">
        <div className="col-12">
          <label htmlFor="inputName4" className="form-label">
            | Donor Name |
          </label>
          <input
            placeholder="Enter Your Name"
            type="text"
            className="form-control"
            id="inputName4"
            onChange={handleInput}
            name="name"
            value={donor.name}
            required
          />
        </div>

        <div className="col-12">
          <label htmlFor="inputGender4">| Gender |</label>
          <select
            type="input"
            onChange={handleInput}
            name="gender"
            value={donor.gender}
            className="form-control"
            aria-label=".form-select-sm example"
            required
          >
            <option selected>Choose your gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div className="col-12">
          <label htmlFor="inputBlood4">| Blood Group |</label>
          <select
            type="input"
            onChange={handleInput}
            name="bloodgroup"
            value={donor.bloodgroup}
            className="form-control"
            aria-label=".form-select-sm example"
            required
          >
            <option>Choose your blood group</option>
            {bgroup.map((el) => (
              <option value={el}>{el}</option>
            ))}
          </select>
        </div>

        <div className="col-12">
          <label htmlFor="inputBirth4">| Birth Year |</label>
          <select
            type="input"
            onChange={handleInput}
            name="birthyear"
            value={donor.birthyear}
            className="form-control"
            aria-label=".form-select-sm example"
            required
          >
            <option>Choose your birth year</option>
            {year.map((el) => (
              <option value={el}>{el}</option>
            ))}
          </select>
        </div>

        <div className="col-12">
          <label htmlFor="inputMobile4" className="form-label">
            | Mobile No. |
          </label>
          <input
            maxLength={10}
            placeholder="Enter Your Mobile No."
            type="number"
            className="form-control"
            id="inputMobile4"
            onChange={handleInput}
            name="mobile"
            value={donor.mobile}
            required
          />
        </div>

        <div className="col-12">
          <label htmlFor="inputEmail4" className="form-label">
            | Email Id |
          </label>
          <input
            placeholder="Enter Your Email Id"
            type="email"
            className="form-control"
            onChange={handleInput}
            name="email"
            value={donor.email}
            id="inputEmail4"
            required
          />
        </div>

        <label htmlFor="inputDistrict4" className="form-label">
          | Select District |
        </label>
        <select
          className="form-control"
          placeholder="Address"
          onChange={handleInput}
          name="address"
          value={donor.address}
          required
        >
          <option disabled value="">
            -----Select-----
          </option>
          {dist.sort().map((el) => (
            <option value={el}>{el}</option>
          ))}
        </select>
        <div className="col-12">
          <button type="submit" className="btn btn-danger">
            SUBMIT
          </button>
        </div>
      </form>
    </>
  );
};
